import { Component } from '@angular/core';

export interface CalculatorButton {
  title: string;
  value: string;
  isOperator?: boolean;
  size?: 'small' | 'medium' | 'large';
}

@Component({
  selector: 'app-keypad',
  templateUrl: './keypad.component.html',
  styleUrls: ['./keypad.component.scss'],
})
export class KeypadComponent {
  calculatorButtons: CalculatorButton[] = [
    { title: 'C', value: 'clear', isOperator: true, size: 'medium' },
    { title: '←', value: 'backspace', isOperator: true },
    { title: '÷', value: 'divide', isOperator: true },
    { title: '7', value: '7' },
    { title: '8', value: '8' },
    { title: '9', value: '9' },
    { title: '×', value: 'multiply', isOperator: true },
    { title: '4', value: '4' },
    { title: '5', value: '5' },
    { title: '6', value: '6' },
    { title: '-', value: 'subtract', isOperator: true },
    { title: '1', value: '1' },
    { title: '2', value: '2' },
    { title: '3', value: '3' },
    { title: '+', value: 'add', isOperator: true },
    { title: '0', value: '0', size: 'medium' },
    { title: '.', value: 'decimalPoint' },
    { title: '=', value: 'equal', isOperator: true },
  ];
}
