import { Component, Input, OnInit } from '@angular/core';

import { AppState } from 'src/app/types/appState.interface';
import { Store } from '@ngrx/store';
import { CalculatorStateAction } from '../../state/calculator.actions';

export interface KeypadButton {
  title: string;
  value: string;
  isOperator?: boolean;
  size?: 'small' | 'medium' | 'large';
}

@Component({
  selector: 'app-keypad-button',
  templateUrl: './keypad-button.component.html',
  styleUrls: ['./keypad-button.component.scss'],
})
export class KeypadButtonComponent {
  @Input() buttonConfig: KeypadButton;

  constructor(private store: Store<AppState>) {}

  public onButtonClick(button: KeypadButton) {
    switch (button.value) {
      case 'clear': {
        this.store.dispatch({
          type: CalculatorStateAction.ClearEquation,
        });
        break;
      }
      case 'equals': {
        this.store.dispatch({
          type: CalculatorStateAction.Equals,
        });
        break;
      }
      case 'backspace': {
        this.store.dispatch({
          type: CalculatorStateAction.Backspace,
        });
        break;
      }
      case '.': {
        const payload = {
          value: button.value,
          isOperator: button.isOperator,
        };
        this.store.dispatch({
          type: CalculatorStateAction.DecimalPoint,
          payload,
        });
        break;
      }
      default: {
        const payload = {
          value: button.value,
          isOperator: button.isOperator,
        };
        this.store.dispatch({
          type: CalculatorStateAction.UpdateEquation,
          payload,
        });
      }
    }
  }
}
