import {
  CalculatorStateActionsUnion,
  CalculatorStateAction,
} from './calculator.actions';

const initialState = '0';

export const calculatorEquationReducer = (
  state = initialState,
  action: CalculatorStateActionsUnion
) => {
  switch (action.type) {
    case CalculatorStateAction.UpdateEquation: {
      if (!validateEquation(action.payload)) {
        return;
      }
      if (state === initialState) {
        return action.payload;
      }
      return state + action.payload;
    }

    case CalculatorStateAction.ClearEquation: {
      return initialState;
    }

    case CalculatorStateAction.Backspace: {
      const updatedState = state.substring(0, state.length - 1);
      if (!updatedState) {
        return initialState;
      }

      return updatedState;
    }

    case CalculatorStateAction.DecimalPoint: {
      if (!state.includes('.')) {
        return state + action.payload;
      }
      return state;
    }

    case CalculatorStateAction.Equals: {
      return String(eval(state));
    }

    default: {
      return state;
    }
  }
};

function validateEquation(equation: string) {
  return equation.length <= 16;
}
