import { createReducer, on } from '@ngrx/store';
import { updateEquation } from './calculator.actions';
import { CalculatorState } from './calculatorState.interface';

export const initialState: CalculatorState = {
  equation: '0',
};

export const calculatorReducer = createReducer(
  initialState,
  on(updateEquation, (state: CalculatorState) => ({ ...state, equation: '1' }))
);
