import { Component } from '@angular/core';
import { KeypadButton } from '../keypad-button/keypad-button.component';

@Component({
  selector: 'app-keypad',
  templateUrl: './keypad.component.html',
  styleUrls: ['./keypad.component.scss'],
})
export class KeypadComponent {
  keypadButtons: KeypadButton[] = [
    { title: 'C', value: 'clear', isOperator: true, size: 'medium' },
    { title: '←', value: 'backspace', isOperator: true },
    { title: '÷', value: '/', isOperator: true },
    { title: '7', value: '7' },
    { title: '8', value: '8' },
    { title: '9', value: '9' },
    { title: '×', value: '*', isOperator: true },
    { title: '4', value: '4' },
    { title: '5', value: '5' },
    { title: '6', value: '6' },
    { title: '-', value: '-', isOperator: true },
    { title: '1', value: '1' },
    { title: '2', value: '2' },
    { title: '3', value: '3' },
    { title: '+', value: '+', isOperator: true },
    { title: '0', value: '0', size: 'medium' },
    { title: '.', value: '.' },
    { title: '=', value: 'equals', isOperator: true },
  ];
}
