import { CalculatorState } from './calculatorState.interface';
import { CalculatorStateAction, UpdateEquation } from './calculator.actions';

export const initialState: CalculatorState = {
  equation: '0',
};

export const calculatorReducer = (
  state = initialState,
  action: UpdateEquation
) => {
  switch (action.type) {
    case CalculatorStateAction.UpdateEquation: {
      return {
        ...state,
        equation: action.payload.equation,
      };
    }
    default: {
      return state;
    }
  }
};
