import { Component } from '@angular/core';

interface CalculatorButton {
  title: string;
  value: string;
  isOperator?: boolean;
}

@Component({
  selector: 'app-keypad',
  templateUrl: './keypad.component.html',
  styleUrls: ['./keypad.component.scss'],
})
export class KeypadComponent {
  calculatorButtons: CalculatorButton[] = [
    { title: 'clear', value: 'C', isOperator: true },
    { title: 'backspace', value: '←', isOperator: true },
    { title: 'divide', value: '÷', isOperator: true },
    { title: '7', value: '7' },
    { title: '8', value: '8' },
    { title: '9', value: '9' },
    { title: 'multiply', value: 'X', isOperator: true },
    { title: '4', value: '4' },
    { title: '5', value: '5' },
    { title: '6', value: '6' },
    { title: 'subtract', value: '-', isOperator: true },
    { title: '1', value: '1' },
    { title: '2', value: '2' },
    { title: '3', value: '3' },
    { title: 'add', value: '+', isOperator: true },
    { title: '0', value: '0' },
    { title: 'decimalPoint', value: '.' },
    { title: 'equal', value: '=', isOperator: true },
  ];
}
