import {
  CalculatorStateActionsUnion,
  CalculatorStateAction,
} from './calculator.actions';

const initialState = '0';

export const calculatorEquationReducer = (
  state = initialState,
  action: CalculatorStateActionsUnion
) => {
  switch (action.type) {
    case CalculatorStateAction.UpdateEquation: {
      const { value, isOperator } = action.payload;
      const lastValue = state.slice(-1);
      const isDoubleOperator = isOperator && getIsOperator(lastValue);
      if (state === initialState) {
        return value;
      }

      if (isDoubleOperator) {
        return state;
      }

      return state + value;
    }

    case CalculatorStateAction.ClearEquation: {
      return initialState;
    }

    case CalculatorStateAction.Backspace: {
      const updatedState = trimLast(state);
      if (!updatedState) {
        return initialState;
      }

      return updatedState;
    }

    case CalculatorStateAction.DecimalPoint: {
      if (!state.includes('.')) {
        return state + action.payload.value;
      }

      return state;
    }

    case CalculatorStateAction.Equals: {
      const equation: string = getIsOperator(state.slice(-1))
        ? trimLast(state)
        : state;

      return String(eval(equation));
    }

    default: {
      return state;
    }
  }
};

const getIsOperator = (value: string): boolean => !isNaN(Number(value));
const trimLast = (value: string): string =>
  value.substring(0, value.length - 1);
