import { CalculatorStateAction, UpdateEquation } from './calculator.actions';

const initialState = '0';

export const calculatorEquationReducer = (
  state = initialState,
  action: UpdateEquation
) => {
  switch (action.type) {
    case CalculatorStateAction.UpdateEquation: {
      return action.payload;
    }
    case CalculatorStateAction.ClearEquation: {
      return initialState;
    }
    default: {
      return state;
    }
  }
};
