import {
  CalculatorStateActionsUnion,
  CalculatorStateAction,
} from './calculator.actions';

const initialState = '0';

export const calculatorEquationReducer = (
  state = initialState,
  action: CalculatorStateActionsUnion
) => {
  switch (action.type) {
    case CalculatorStateAction.UpdateEquation: {
      const { value, isOperator } = action.payload;
      const isDoubleOperator = isOperator && getIsLastValueOperator(state);
      const updatedState = isDoubleOperator ? trimLastValue(state) : state;
      if (state === initialState) {
        return value;
      }

      return updatedState + value;
    }

    case CalculatorStateAction.ClearEquation: {
      return initialState;
    }

    case CalculatorStateAction.Backspace: {
      const updatedState = trimLastValue(state);
      if (!updatedState) {
        return initialState;
      }

      return updatedState;
    }

    case CalculatorStateAction.DecimalPoint: {
      if (getIsValidDecimalPoint(state)) {
        return state + action.payload.value;
      }

      return state;
    }

    case CalculatorStateAction.Equals: {
      const equation: string = getIsLastValueOperator(state)
        ? trimLastValue(state)
        : state;

      return String(eval(equation));
    }

    default: {
      return state;
    }
  }
};

const getIsLastValueOperator = (state: string): boolean => {
  return isNaN(Number(state.slice(-1)));
};
const trimLastValue = (state: string): string => {
  return state.substring(0, state.length - 1);
};
const getIsValidDecimalPoint = (state: string): boolean => {
  const lastDecimalPointIndex: number = state.lastIndexOf('.');
  const hasEndingOperator: boolean = getIsLastValueOperator(state);
  const hasDecimalPoint: boolean = lastDecimalPointIndex !== -1;
  const isDoubleDecimalPoint: boolean =
    hasDecimalPoint &&
    state
      .split('')
      .splice(lastDecimalPointIndex + 1)
      .every(Number);

  if (hasEndingOperator || isDoubleDecimalPoint) {
    return false;
  }

  return true;
};
