import { createSelector } from '@ngrx/store';
import { AppState } from 'src/app/types/appState.interface';
import { CalculatorState } from './calculatorState.interface';

export const selectFeature = (state: AppState) => state.calculator;

export const equationSelector = createSelector(
  selectFeature,
  (state: CalculatorState) => state.equation
);
