import { Component, Input } from '@angular/core';
import { Observable } from 'rxjs';

import { CalculatorButton } from '../keypad/keypad.component';
import { AppState } from 'src/app/types/appState.interface';
import { Store, select } from '@ngrx/store';
import { equationSelector } from '../../state/calculator.selectors';
import { CalculatorStateAction } from '../../state/calculator.actions';

@Component({
  selector: 'app-keypad-button',
  templateUrl: './keypad-button.component.html',
  styleUrls: ['./keypad-button.component.scss'],
})
export class KeypadButtonComponent {
  @Input() buttonConfig: CalculatorButton;

  equation$: Observable<string>;

  constructor(private store: Store<AppState>) {}

  ngOnInit(): void {
    this.equation$ = this.store.pipe(select(equationSelector));
  }

  onEquationChange(payload: string) {
    this.store.dispatch({
      type: CalculatorStateAction.UpdateEquation,
      payload,
    });
  }

  public onButtonClick(value: string) {
    this.onEquationChange(this.equation$ + value);
  }
}
